//The iOS switch directive
angular.module('iosSwitch', []).directive('iosSwitch', function () {
  return {
    require: 'ngModel',
	restrict: 'A',
       scope:{           
    	   ngModel: '=' ,
    	  on: '=?',
    	   off: '=?'
       },
       
       template: "<div class='switchIos toggle-light' style='height: 20px; width: 60px;'>"+
		       	"	<div class='toggle-slide' ng-click='toggleSwitch'> " +
			    "	<div class='toggle-inner' ng-class='{off: (ngModel ? false : true)}'   style='width: 100px;'>" +
		        "<div class='toggle-on active' ng-class='{active: ngModel}'  style='height: 20px; width: 60px; text-indent: -17.66667px; line-height: 20px;'>{{on}}</div>" + 
		        
	               "<div class='toggle-blob' style='height: 20px; width: 20px; margin-left: -20px;'></div>" + 
	               	"<div class='toggle-off' ng-class='{active: (ngModel ? false : true) }'  style='height: 20px; width: 50px; margin-left: -10px; text-indent: 6.66667px; line-height: 20px;'>{{off}}</div>" +                                        
	            "</div>" +
	          "</div>" +
	       "</div>" ,
	       
	     controller: function($scope, $document, $window){
	    	
	    	if($scope.on == undefined ){
	    		$scope.on = 'oui'; 
	    	}
	    	
	 		if($scope.off == undefined) {
	 			$scope.off = 'non'; 
	    	}
	     }

		
  };
});
